document.addEventListener('DOMContentLoaded', function() {
    const riveScript = document.createElement('script');
    riveScript.src = 'https://unpkg.com/@rive-app/canvas@latest';
    riveScript.onload = function() {
        console.log('Rive runtime loaded');
        initRive();
    };
    document.head.appendChild(riveScript);

    const RIVE_FILE = 'https://cdn.prod.website-files.com/67880ff570cdb1a85eee946f/679688a2402a3971718a3ff2_combined_sdk.riv';

    function initRive() {
        console.log('initRive called');

        function handleTabChange(tabId) {
            console.log('Tab change triggered for:', tabId);

            // Find the specific pane for this tab
            const targetPane = document.querySelector(`.lang-tabs .product-tabs_pane[data-w-tab="${tabId}"]`);
            console.log('Found target pane:', !!targetPane);

            // Create intersection observer for the first load
            const observer = new IntersectionObserver((entries) => {
                entries.forEach(entry => {
                    if (entry.isIntersecting) {
                        // Once in view, initialize the animation
                        if (targetPane.classList.contains('w--tab-active')) {
                            console.log('Pane is now active and visible for:', tabId);
                            initAnimation(targetPane, tabId);
                        } else {
                            checkPane();
                        }
                        // Disconnect the observer after first intersection
                        observer.disconnect();
                    }
                });
            }, { threshold: 0.4 }); // 1.0 means fully in view

            // Wait for the pane to become active
            const checkPane = () => {
                if (targetPane.classList.contains('w--tab-active')) {
                    console.log('Pane is now active for:', tabId);
                    initAnimation(targetPane, tabId);
                } else {
                    console.log('Waiting for pane to become active...');
                    setTimeout(checkPane, 50);
                }
            };

            // Get the first tab's ID
            const firstTabId = document.querySelector('.product-tabs_link.is-lang').getAttribute('data-w-tab');

            // If it's the first tab AND it's the initial page load, use intersection observer
            if (tabId === firstTabId && !window.hasInteractedWithTabs) {
                observer.observe(targetPane);
            } else {
                // For tab clicks or subsequent visits to first tab, just use the regular check
                checkPane();
            }
        }

        function initAnimation(activePane, tabId) {
            const riveContainer = activePane.querySelector('.product-tabs_pane-image.shadow-main .product-tabs_code-block .rive-canvas');
            console.log('Rive container found:', !!riveContainer);

            if (riveContainer) {
                const animationContainer = document.createElement('div');
                animationContainer.style.cssText = 'width: 100%; height: 100%';

                let canvas = document.createElement('canvas');
                canvas.style.cssText = 'border: none; width: 100%; height: 100%';
                canvas.setAttribute('allowfullscreen', '');
                canvas.setAttribute('allow', 'autoplay');

                const rect = riveContainer.getBoundingClientRect();
                const width = rect.width;
                const height = rect.height || 400;

                const dpr = window.devicePixelRatio || 1;
                canvas.width = width * dpr;
                canvas.height = height * dpr;

                // Cleanup existing instance before clearing innerHTML
                if (riveContainer._riveInstance) {
                    console.log('Cleaning up previous instance for tab:', tabId);
                    riveContainer._riveInstance.cleanup();
                    riveContainer._riveInstance = null;
                }

                riveContainer.innerHTML = '';
                animationContainer.appendChild(canvas);
                riveContainer.appendChild(animationContainer);

                try {
                    console.log('Creating new Rive instance for tab:', tabId);
                    const r = new rive.Rive({
                        canvas: canvas,
                        src: RIVE_FILE,
                        autoplay: true,
                        artboard: 'Action',
                        stateMachines: ['StateMachine'],
                        fit: rive.Fit.Cover,
                        alignment: rive.Alignment.Center,
                        onLoad: () => {
                            console.log('Animation loaded for', tabId);
                            riveContainer._riveInstance = r;

                            try {
                                const inputs = r.stateMachineInputs('StateMachine');
                                console.log('Available inputs:', inputs);
                                if (inputs) {
                                    // Debug the tab selection
                                    const tab = document.querySelector(`.product-tabs_link.is-lang[data-w-tab="${tabId}"]`);
                                    console.log('Selected tab element:', tab);
                                    const triggerName = tab.getAttribute('data-language');
                                    console.log('Trying to fire trigger:', triggerName);
                                    const trigger = inputs.find(i => i.name === triggerName);
                                    console.log('Found trigger:', trigger);
                                    if (trigger) {
                                        trigger.fire();
                                        console.log('Trigger fired:', triggerName);
                                    } else {
                                        console.warn('Trigger not found:', triggerName);
                                    }
                                }
                            } catch (e) {
                                console.error('State machine trigger error:', e);
                                console.log('Tab ID:', tabId);
                                console.log('Tab element:', document.querySelector(`[data-w-tab="${tabId}"]`));
                            }
                        }
                    });

                } catch (error) {
                    console.error('Rive error:', error);
                }
            }
        }

        // Listen for tab clicks
        const tabLinks = document.querySelectorAll('.product-tabs_link.is-lang');
        console.log('Found tab links:', tabLinks.length);
        tabLinks.forEach(tab => {
            tab.addEventListener('click', function() {
                window.hasInteractedWithTabs = true;  // Set flag when user interacts
                const tabId = this.getAttribute('data-w-tab');
                console.log('Tab clicked:', tabId);
                handleTabChange(tabId);
            });
        });

        // Initialize first tab
        setTimeout(() => {
            console.log('Initializing first tab');
            const firstTab = document.querySelector('.product-tabs_link.is-lang');
            if (firstTab) {
                const tabId = firstTab.getAttribute('data-w-tab');
                console.log('First tab ID:', tabId);
                handleTabChange(tabId);
            } else {
                console.log('No first tab found');
            }
        }, 200);
    }
});
