    class RiveTabsAnimationController {
        constructor() {
            this.riveInstance = null;
            this.canvas = null;
            this.stateMachineInstance = null;
            this.activeButton = null;
            this.activeImage = null;
            this.explosionInstance = null;
            this.hasTriggeredInitialAnimation = false;
        }

        async init() {
            const animationContent = `
            <div id="rive-animation-container-tabs" style="width: 100%; height: 100%">
                <canvas id="rive-canvas-tabs" style="border: none; width: 100%; height: 100%" allowfullscreen allow="autoplay"></canvas>
</div>
        `;

            document.getElementById('animation-wrapper-tabs').innerHTML = animationContent;
            await this.loadRiveScript();
            await this.setupRiveInstance();
            await this.setupExplosionInstance();
            this.setupIntersectionObserver();
        }

        loadRiveScript() {
            if (window.rive) {
                return Promise.resolve();
            }

            return new Promise((resolve, reject) => {
                const riveScript = document.createElement('script');
                riveScript.src = 'https://unpkg.com/@rive-app/canvas@latest';
                riveScript.onload = resolve;
                riveScript.onerror = reject;
                document.body.appendChild(riveScript);
            });
        }

        async setupRiveInstance() {
            return new Promise((resolve, reject) => {
                try {
                    this.canvas = document.getElementById("rive-canvas-tabs");

                    if (!this.canvas) {
                        throw new Error("Canvas element not found");
                    }

                    const width = this.canvas.offsetWidth;
                    const height = this.canvas.offsetHeight;
                    this.canvas.width = width * 2;
                    this.canvas.height = height * 2;

                    this.riveInstance = new rive.Rive({
                        canvas: this.canvas,
                        src: "https://cdn.prod.website-files.com/67880ff570cdb1a85eee946f/678f0ce91ec9565c0845f0b4_how-it-works-paths.riv",
                        autoplay: true,
                        fit: rive.Fit.Cover,
                        artboard: 'Main',
                        stateMachines: ['StateMachine'],
                        onLoad: resolve
                    });
                } catch (error) {
                    reject(error);
                }
            });
        }

        async setupExplosionInstance() {
            return new Promise((resolve, reject) => {
                try {
                    const explosionCanvas = document.createElement('canvas');
                    explosionCanvas.style.cssText = 'width: 100%; height: 100%; display: block;';

                    const container = document.querySelector('.rive-explosion-particles');
                    if (!container) {
                        throw new Error("Explosion container not found");
                    }
                    container.appendChild(explosionCanvas);

                    // Get the container dimensions
                    const width = container.offsetWidth;
                    const height = container.offsetHeight;
                    explosionCanvas.width = width * 2;  // For high DPI displays
                    explosionCanvas.height = height * 2;

                    this.explosionInstance = new rive.Rive({
                        canvas: explosionCanvas,
                        src: "https://cdn.prod.website-files.com/67880ff570cdb1a85eee946f/6799807526086bece863d99f_particles_explosion_without_trigger.riv",  //"https://cdn.prod.website-files.com/677dd0b17c7f7212ee5cb6a5/678d70fce395bea79bc4c140_explosion-particles-3.riv"
                        autoplay: false,
                        fit: rive.Fit.Contain,
                        alignment: rive.Alignment.Center,
                        artboard: 'Explosion',
                        stateMachines: ['StateMachine'],
                        onLoad: resolve
                    });
                } catch (error) {
                    reject(error);
                }
            });
        }

        playExplosion() {
            if (this.explosionInstance) {
                this.explosionInstance.stop('StateMachine');
                this.explosionInstance.play('StateMachine');
            }
        }

        fireTrigger(triggerName) {
            if (this.riveInstance && this.riveInstance.animator) {
                try {
                    const stateMachine = this.riveInstance.animator.stateMachines[0];
                    const inputs = stateMachine.inputs;

                    if (inputs) {
                        const input = inputs.find(i => i.name === triggerName);
                        if (input) {
                            input.fire();
                        }
                    }
                } catch (error) {
                    console.error("Error firing trigger:", error);
                }
            }
        }

        setupTriggerLinks() {
            const links = document.querySelectorAll('[data-rive-tabs-trigger]');

            links.forEach(link => {
                link.addEventListener('click', (e) => {
                    e.preventDefault();
                    const triggerName = link.getAttribute('data-rive-tabs-trigger');
                    const imageSelector = link.getAttribute('data-tab-image');

                    // Handle button active state immediately
                    if (this.activeButton) {
                        this.activeButton.classList.remove('active');
                    }
                    link.classList.add('active');
                    this.activeButton = link;

                    // Add grayscale to all currently active elements before transition
                    const currentActiveElements = document.querySelectorAll('.tab-control.active');
                    if (currentActiveElements.length) {
                        // Remove any initial grayscale class first
                        document.querySelectorAll('.tab-control').forEach(el => {
                            el.classList.remove('grayscale-image');
                        });
                        // Then add it to all current active elements
                        currentActiveElements.forEach(el => {
                            el.classList.add('grayscale-image');
                        });
                    }

                    // Fire the Rive trigger
                    this.fireTrigger(triggerName);

                    setTimeout(() => {
                        this.playExplosion();

                        // Handle all elements with tab-control class
                        const currentElements = document.querySelectorAll('.tab-control.active');
                        currentElements.forEach(el => {
                            el.classList.remove('active');
                            el.classList.remove('grayscale-image');
                        });

                        // Set new active elements
                        const targetElements = document.querySelectorAll(`.tab-control[data-tab-image="${imageSelector}"]`);
                        targetElements.forEach(el => el.classList.add('active'));

                    }, 800);
                });
            });
        }

        setupIntersectionObserver() {
            const observer = new IntersectionObserver((entries) => {
                entries.forEach(entry => {
                    if (entry.isIntersecting && !this.hasTriggeredInitialAnimation) {
                        // Find the TypeScript trigger
                        const typescriptTrigger = document.querySelector('[data-rive-tabs-trigger="Typescript"]');
                        if (typescriptTrigger) {
                            typescriptTrigger.click(); // Simulate click
                            this.hasTriggeredInitialAnimation = true;
                        }
                    }
                });
            }, {
                threshold: 1.0
            });

            // Observe the animation wrapper
            const wrapper = document.getElementById('animation-wrapper-tabs');
            if (wrapper) {
                observer.observe(wrapper);

                // Check initial position
                setTimeout(() => {
                    const rect = wrapper.getBoundingClientRect();
                    const isVisible = rect.top < window.innerHeight && rect.bottom >= 0;

                    if (isVisible && !this.hasTriggeredInitialAnimation) {
                        const typescriptTrigger = document.querySelector('[data-rive-tabs-trigger="Typescript"]');
                        if (typescriptTrigger) {
                            typescriptTrigger.click();
                            this.hasTriggeredInitialAnimation = true;
                        }
                    }
                }, 100); // Small delay to ensure everything is loaded
            }
        }
    }

    document.addEventListener('DOMContentLoaded', async () => {
        try {
            const tabsAnimation = new RiveTabsAnimationController();
            await tabsAnimation.init();
            tabsAnimation.setupTriggerLinks();
            window.riveTabsAnimation = tabsAnimation;
        } catch (error) {
            console.error("Error setting up animation:", error);
        }
    });
