  document.addEventListener("DOMContentLoaded", function () {
    const autoTabsElements = document.querySelectorAll('[data-auto-tabs="true"]');

    autoTabsElements.forEach((tabsElement) => {
      const tabLinks = tabsElement.querySelectorAll('.w-tab-link');
      const tabContainer = tabLinks[0]?.parentElement; // Assumes tabs are within a parent container
      let currentTabIndex = 0;
      const rotationDelay = 7000; // Set the delay for tab rotation (7 seconds)
      let intervalId;

      // Function to scroll the parent container to position the active tab
      function scrollToActiveTab(tabLink) {
        if (tabContainer) {
          const containerRect = tabContainer.getBoundingClientRect();
          const tabRect = tabLink.getBoundingClientRect();
          const scrollLeft = tabContainer.scrollLeft + (tabRect.left - containerRect.left) - (window.innerWidth * 0.05);
          tabContainer.scrollTo({ left: scrollLeft, behavior: "smooth" });
        }
      }

      // Function to activate the next tab
      function activateNextTab() {
        const currentTabLink = tabLinks[currentTabIndex];
        const nextTabIndex =
          currentTabLink.nextElementSibling &&
          currentTabLink.nextElementSibling.classList.contains('w-tab-link')
            ? currentTabIndex + 1
            : 0; // Go to the first tab if no next sibling exists

        currentTabIndex = nextTabIndex;
        tabLinks[currentTabIndex].click(); // Activate the next tab
        scrollToActiveTab(tabLinks[currentTabIndex]); // Adjust scroll position
      }

      // Start the auto-rotation and mark tabs as active
      function startAutoRotation() {
        tabsElement.classList.add("is-active"); // Mark the tabs element as active
        intervalId = setInterval(activateNextTab, rotationDelay);
      }

      // Stop the auto-rotation
      function stopAutoRotation() {
        clearInterval(intervalId);
      }

      // Restart auto-rotation when the user manually interacts
      function handleUserInteraction(index) {
        if (index !== currentTabIndex) {
          stopAutoRotation();
          currentTabIndex = index; // Update the current tab index
          scrollToActiveTab(tabLinks[currentTabIndex]); // Adjust scroll position
          tabsElement.setAttribute('data-auto-tabs', 'false'); // Stops all animations
          // startAutoRotation();
        }
      }

      // Add event listeners to handle user clicks
      tabLinks.forEach((tabLink, index) => {
        tabLink.addEventListener("click", () => {
          handleUserInteraction(index);
        });
      });

      // Start rotation only when the tabs element enters the viewport
      const observer = new IntersectionObserver(
        (entries) => {
          entries.forEach((entry) => {
            if (entry.isIntersecting) {
              startAutoRotation();
              observer.disconnect(); // Stop observing after starting the rotation
            }
          });
        },
        { threshold: 0.1 } // Trigger when 10% of the element is visible
      );

      observer.observe(tabsElement);
    });
  });




