	document.addEventListener("DOMContentLoaded", function () {
    const logoItems = document.querySelectorAll("[sw-logo-item]");
    const maxDisplayItems = 8;
    let allLogos = [];

    if (logoItems.length <= maxDisplayItems) return; // No need to move images if there are 8 or fewer items

    // Step 1: Collect all logos
    logoItems.forEach((item) => {
        const img = item.querySelector("img");
        if (img) {
            allLogos.push(img);
        }
    });

    // Step 2: Move extra logos (9th and beyond) into the first 8 items cyclically
    allLogos.forEach((img, index) => {
        if (index >= maxDisplayItems) {
            img.classList.remove("is-active"); // Remove is-active class
            const targetIndex = index % maxDisplayItems; // Assign to the correct target item
            logoItems[targetIndex].appendChild(img);
        }
    });

    // Step 3: Store the first 8 original logos before duplication
    let originalFirst8 = [];
    logoItems.forEach((item, index) => {
        let firstImg = item.querySelector("img");
        if (firstImg) originalFirst8.push(firstImg); // Save the original logo for duplication
    });

    // Step 4: Ensure each of the first 8 has at least two logos (without duplicating its own)
    let nextDuplicateIndex = 0; // Start from the FIRST logo

    logoItems.forEach((item, index) => {
        const images = item.querySelectorAll("img");
        if (images.length < 2) {
            // Find the next logo in sequence that is NOT from the same index
            while (nextDuplicateIndex === index) {
                nextDuplicateIndex = (nextDuplicateIndex + 1) % maxDisplayItems; // Skip self-duplication
            }

            let clonedImg = originalFirst8[nextDuplicateIndex].cloneNode(true);
            clonedImg.classList.remove("is-active"); // Ensure it's inactive
            item.appendChild(clonedImg);

            // Move to the next logo for duplication
            nextDuplicateIndex = (nextDuplicateIndex + 1) % maxDisplayItems;
        }
    });

    // Function to cycle through images in a staggered animation
    function cycleLogos() {
        logoItems.forEach((item, index) => {
            setTimeout(() => {
                const images = item.querySelectorAll("img");
                if (images.length > 1) {
                    const activeImg = item.querySelector("img.is-active");
                    if (activeImg) {
                        activeImg.classList.remove("is-active");
                        let nextImg = activeImg.nextElementSibling || images[0];
                        nextImg.classList.add("is-active");
                    } else {
                        images[0].classList.add("is-active"); // Ensure at least one active image
                    }
                }
            }, index * 100); // Staggered by 0.075s per item
        });
    }

    // Start the animation loop
    setInterval(cycleLogos, 5000);
});

